mapper.update:
update t_sys_shopping_car
set
shop_num=#{shopNum},
updated_time=now(),
updated_by=#{updatedBy}
where
is_delete=0
and sku_code=#{skuCode}
and business_code=#{businessCode}
and order_user_id=#{orderUserId}

mapper.delete:
delete from t_sys_shopping_car
where
  is_delete=0
  and sku_code=#{skuCode}
  and order_user_id=#{orderUserId}
  and business_code=#{businessCode}
